    margin-bottom: 35px;
    @include mqTB{
      padding: calc-vw(25,1280);
      margin-bottom: calc-vw(35,1280);
    }
    @include mqTB{
      padding: calc-vw(10,320);
      margin-bottom: calc-vw(26,320);
    }
    h4{
      margin-bottom: 15px;
    }
    p{
      margin-bottom: inherit;
      &:last-child {
        margin-bottom: 0;
      }
    }
  }

  ol {
    counter-reset: number;
    margin-bottom: 35px;
    @include mqTB{
      margin-bottom: calc-vw(35,1280);
    }
    @include mqSP{
      margin-bottom: calc-vw(20,320);
    }
    > li {
      font-size: 15px;
      margin-bottom: 10px;
      text-indent: -24px;
      padding-left: 24px;
      @include mqTB {
        font-size: calc-vw(15,1280);
        margin-bottom: calc-vw(10,1280);
      }
      @include mqSP {
        font-size: 14px;
        margin-bottom: calc-vw(20);
        text-indent: -23px;
        padding-left: 23px;
      }
      &:last-child {
        margin-bottom: 0;
      }
      &::before {
        counter-increment: number;
        content: counter(number) ".";
        text-align: center;
        margin-right: 10px;
        font-weight: bold;
      }
    }
  }

  ul {
    color: $c_k;
    margin-bottom: 35px;
    font-size: 15px;

    @include mqTB {
      margin-bottom: calc-vw(35,1280);
      font-size: calc-vw(15,1280);
    }
    @include mqSP {
      font-size: 14px;
    }
    > li{
      margin-bottom: 10px;
      text-indent: -15px;
      padding-left: 15px;
      @include mqTB {
        margin-bottom: calc-vw(10,1280);
      }
      @include mqSP {
        margin-bottom: calc-vw(20);
        text-indent: -16px;
        padding-left: 16px;
      }
      &:before {
        content: "";
        height: 6px;
        width: 6px;
        border-radius: 50%;
        display: inline-block;
        vertical-align: middle;
        margin-right: 10px;
        background-color: $c_k;
      }

      &:last-child {
        margin-bottom: 0;
      }
    }
  }

  .titleListBox{
    div.titleListBox-inner{
      ul{
        margin-bottom: 0;
        >li{
          text-indent: inherit;
          padding-left: inherit;
          a{
            color: $c_k;
            @include mqTB{
              font-size: calc-vw(18,1280);
            }
            @include mqSP{
              font-size: 17px;
            }
          }
          &:before {
            display: none;
          }
        }
      }
    }
  }
}

.iframeWrap {
  position: relative;
  width: 100%;
  padding: 56.25% 0 0 0;
  margin-bottom: 40px;
  @include mqTB {
    margin-bottom: calc-vw(40,1280);
  }
  @include mqSP {
    margin-bottom: calc-vw(40);
  }
}
.iframeWrap iframe {
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
}
