sections.css:
.text_letter {
  @include f_base;
  font-size: 15px;
  font-weight: normal;
  line-height: 1.75;
  @include mqTB {
    font-size: calc-vw(15,1280);
  }
  @include mqSP {
    font-size: 14px;
  }
  .mypage-cancellation-description & {
    margin-bottom: 40px;
    @include mqSP {
      margin-bottom: calc-vw(60);
      &:last-child {
        margin-bottom: calc-vw(80);
      }
    }
  }
  .wrap_toFaq & {
    margin-bottom: 40px;
    @include mqTB{
      margin-bottom: calc-vw(40,1280);
    }
    @include mqSP{
      margin-bottom: calc-vw(20,320);
      font-size: 14px;
      line-height: 1.8;
    }
  }
  > a {
    color: $c_link;
    text-decoration: underline;
    @include transition;
    &:hover {
      opacity: $tr_opacity;
    }
  }
  strong {
    font-weight: bold;
  }
}

.text_headingDescription {
  @include f_base;
  font-size: 15px;
  font-weight: normal;
  text-align: center;
  width: 780px;
  margin: 10px auto 60px;
  @include mqTB {
    width: calc-vw(780,1280);
    font-size: calc-vw(15,1280);
    margin-bottom: calc-vw(60,1280);
  }
  @include mqSP {
    font-size: 12px;
    margin: calc-vw(28) calc-vw(30) calc-vw(70);
    width: calc-vw(290,320);
  }
  .heading_section_secondary + & {
    margin-top: -65px;
    @include mqTB {
      margin-top: calc-vw(-65,1280);
    }
    @include mqSP {
      margin-top: calc-vw(-37,320);
    }
  }
}

.text_headingDescription_secondary {
  @extend .text_headingDescription;
  color: $c_gray2;
  margin-top: -65px;
  @include mqTB {
    margin-top: calc-vw(-65,1280);
  }
  @include mqSP {
    margin-top: calc-vw(-60);
  } 
  .gift-description & {
    margin-top: 20px;
    @include mqTB {
      margin-top: calc-vw(20,1280);
    }
  }
  .text_headingDescription + & {
    margin-top: -35px;
    margin-bottom: 110px;
    @include mqTB {
      margin-top: calc-vw(-35,1280);
      margin-bottom: calc-vw(110,1280);
    }
    @include mqSP {
      margin-top: calc-vw(-40);
      margin-bottom: calc-vw(65);
    }
  }
}

.text_summary {
  font-size: 14px;
  text-align: center;
  line-height: 1.75;
  @include mqTB {
    font-size: calc-vw(14,1280);
  }
  @include mqSP {
    font-size: 12px;
  }
  > a {
    color: $c_link;
    text-decoration: underline;
    @include transition;
    &:hover {
      opacity: $tr_opacity;
    }
  }
  strong {
    font-weight: bold;
  }
}

.text_formDescription {
  font-size: 15px;
  margin-bottom: 40px;
  padding: 0 20px;
  line-height: 1.75;
  span {
    color: $c_red_dark;
    font-weight: bold;
  }
  p {
    margin-bottom: 30px;
    &:last-child {
      margin-bottom: 0;
    }
  }
  @include mqTB {
    font-size: calc-vw(15,1280);
  }
  @include mqSP {
    margin-top: calc-vw(60);
    margin-bottom: calc-vw(60);
    padding: 0 calc-vw(30);
    font-size: 14px;
  }
  .form-item_confirm & {
    padding: 0;
    @include mqSP {
      margin-top: 0;
      &:last-child {
        margin-bottom: 0;
      }
    }
  }
  .registration-section_complete & {
    margin-bottom: 60px;
    text-align: center;
  }
  .page-reminder & {
    @include mqSP {
      margin: calc-vw(60) 0;
    }
    a {
      color: $c_blue;
      // text-decoration: underline;
      border-bottom: 1px solid $c_blue;
      display: inline-block;
      @include transition;
      line-height: 1;
      &:hover {
        // text-decoration: none;
        border-bottom: 1px solid transparent;
      }
    }
  }
  .page-login & {
    padding: 0;
    a {
      color: $c_blue;
      text-decoration: underline;
      &:hover {
        text-decoration: none;
      }
    }
    &:last-child {
      margin-bottom: 0;
    }
    @include mqSP {
      margin-top: 0;
      &:last-child {
        margin-top: calc-vw(40);
        margin-bottom: calc-vw(60);
      }
    }
  }
  .page-login .login-box &{
    padding: 0 10px;
    text-align: center;
  }
}

.text-formError {
  padding-top: 5px;
  @include f_base;
  font-size: 11px;
  font-weight: bold;
  line-height: 1;
  color: $c_red_dark;
  &:first-of-type {
    border-top: 2px solid $c_red_dark;
  }
}

.text_formNote {
  margin-top: 20px;
  @include f_base;
  font-size: 12px;
  line-height: 1.75;
  > a {
    color: $c_link;
    text-decoration: underline;
    @include transition;
    &:hover {
      opacity: $tr_opacity;
    }
  }
}
.text_formNoteImportant {
  @extend .text-formError;
  margin-top: 20px;
  padding: 0;
  border: none;
  @include f_base;
  color: $c_k;
  font-weight: normal;
  line-height: 1.5;
}

.text_section_description{
  text-align: center;
  padding-bottom: 20px;
  position: relative;
  @include mqTB{
    padding-bottom: calc-vw(20,1280);
  }
  @include mqSP{
    width: 260px;
    margin: 0 auto;
    font-size: 13px;
    padding-bottom: calc-vw(22,320);

    .page-list_girlfriends & {
      padding-bottom: calc-vw(16,320);
    }

    .page-registration & {
      width: calc-vw(290,320);
      font-size: 14px;
      text-align: inherit;
      padding-bottom: calc-vw(30);
      line-height: 1.8;
    }
  }
}

.text_message {
  font-size: 16px;
  line-height: 1.7;
  margin-bottom: 50px;
  @include mqTB {
    font-size: calc-vw(16,1280);
    margin-bottom: calc-vw(50,1280);
  }
  @include mqSP {
    font-size: 14px;
    margin-bottom: calc-vw(50);
    padding: 0 calc-vw(30);
    .section-content & {
      padding: 0;
    }
  }
  .page-list &,
  .page-mypage & {
    text-align: center;
  }
}

.heading_section_primary {
  @include f_poppins-base;
  font-size: 30px;
  font-weight: 600;
  text-align: center;
  position: relative;
  padding-top: 90px;
  overflow: hidden;
  margin-top: -65px;
  background-image: url(../img/section-line.svg);
  background-size: 120px auto;
  background-repeat: no-repeat;
  background-position: left top;
  @include mqTB {
    font-size: calc-vw(30,1280);
    padding-top: calc-vw(90,1280);
    margin-top: calc-vw(-65,1280);
    background-size: calc-vw(120,1280) auto;
  }
  @include mqSP {
    font-size: 20px;
    padding-top: calc-vw(50);
    margin-top: calc-vw(-56);
    background-size: calc-vw(70);
  }
  &:before {
    content: "";
    display: block;
    height: 2px;
    background-color: $c_k;
    width: 100%;
    position: absolute;
    top: 0;
    left: 118px;
    @include mqTB {
      left: calc(#{calc-vw(120,1280)} - 2px);
    }
    @include mqSP {
      height: 1px;
      left: calc(#{calc-vw(70)} - 2px);
      }
  }

  .page-about & {
    margin-top: 0;
    text-transform: uppercase;
    .proper {
      text-transform: none;
    }
  }
}

.heading_section_secondary {
  @include f_poppins-base-b;
  font-size: 24px;
  text-align: center;
  padding-bottom: 63px;
  @include mqTB {
    padding-bottom: calc-vw(76,1280);
    font-size: calc-vw(24,1280);
  }
  @include mqSP {
    padding-bottom: calc-vw(35,320);
    font-size: 18px;
  }
  .page-mypage & {
    border-bottom: 0;
  }
  .page-top & {
    padding-bottom: 60px;
    @include mqTB {
      padding-bottom: calc-vw(60,1280);
    }
    @include mqSP {
      padding-bottom: calc-vw(28,320);
    }
  }

  .detail_girlfriends_related & {
    font-family: $f_base;
    font-size: 20px;
    padding-bottom: 64px;
    @include mqSP{
      font-size: 13px;
      padding-bottom: 34px;
    }
  }
}

.heading_detail_primary {
  @include f_chikumin;
  font-size: 30px;
  letter-spacing: 0.05em;
  text-align: center;
  margin-top: 37px;
  margin-bottom: 20px;
  line-height: 1.8;

  @include mqTB {
    margin-top: calc-vw(50,1280);
    font-size: calc-vw(30,1280);
    margin-bottom: calc-vw(30,1280);
  }
  @include mqSP {
    margin: 0 auto;
    margin-top: calc-vw(20,320);
    margin-bottom: calc-vw(12,320);
    line-height: 1.5;
    font-size: 18px;
  }
}

.heading_appendix{
  font-size: 18px;
  margin: 15px 0;
  font-weight: bold;
  @include mqTB{
  }
  @include mqSP{
    margin: calc-vw(10,320) 0;
  }
}

.heading_page_ja {
  margin-bottom: 40px;
  @include f_chikumin;
  font-size: 25px;
  text-align: center;
  @include mqTB {
    font-size: calc-vw(25,1280);
  }
  @include mqSP {
    margin-bottom: calc-vw(60);
  }
}

.heading_formTitle {
  margin-bottom: 20px;
  font-size: 12px;
  font-weight: bold;
  text-align: left;
  span {
    color: $c_red_dark;
  }
}

.heading_tertiary {
  font-size: 20px;
  text-align: center;
  padding: 30px 0;
  font-weight: bold;
  @include f_base;
  @include mqTB {
    font-size: calc-vw(20, 1280);
    padding: calc-vw(30,1280) 0;
  }
  @include mqSP {
    font-size: 15px;
    padding: calc-vw(50) 0;
    .page-detail_gift & {
      padding: calc-vw(75) 0 calc-vw(30);
    }
  }
  .page-registration & {
    padding: 55px 0;
    @include mqTB {
      padding: calc-vw(55,1280);
    }
    @include mqSP {
      padding: calc-vw(48);
    }
  }
  .registration-schedule & {
    padding: 55px 0 5px;
    @include mqTB {
      padding: calc-vw(55,1280) 0 calc-vw(5,1280);
    }
    @include mqSP {
      padding: calc-vw(65) 0 0;
    }
  }
  .page-detail_gift & {
    @include mqSP {
      font-size: 13px;
      padding-top: 0;
    }
  }
}

.heading_mypage {
  @include f_poppins-base_b;
  padding: 60px 0;
  text-align: center;
  font-size: 24px;
  .l-side & {
    padding-bottom: 20px;
  }
  &:first-child {
    padding-top: 0;
  }
  @include mqTB {
    font-size: calc-vw(24, 1280);
    padding: calc-vw(60,1280) 0;
  }
  @include mqSP {
    font-size: 18px;
    padding: calc-vw(80) 0;
  }
}
.heading_mypage_cancellation {
  @extend .heading_mypage;
  font-size: 20px;
  text-align: center;
  @include mqTB {
    font-size: calc-vw(20, 1280);
  }
  @include mqSP {
    font-size: 15px;
  }
}

.heading_billboard {
  @include f_poppins-base-b;
  padding: 64px 0;
  font-size: 24px;
  text-align: center;
  border-bottom: 1px solid $c_k;
  margin: 0;
  @include mqTB {
    padding: calc-vw(64,1280) 0;
    font-size: calc-vw(24,1280);
  }
  @include mqSP {
    padding-top: calc-vw(28,320);
    padding-bottom: calc-vw(28,320);
    font-size: 18px;
  }
  .page-reminder & {
    margin-bottom: 60px;
    @include mqSP {
      margin: 0;
    }
  }
  .page-login & {
    border-bottom: 0;
  }
}

.heading_quaternary {
  text-decoration: underline;
  font-weight: bold;
  margin-bottom: 10px;
  font-size: 15px;
  @include mqTB {
    margin-bottom: calc-vw(10,1280);
    font-size: calc-vw(15,1280);
  }
  @include mqSP {
    margin-bottom: calc-vw(10);
    font-size: 13px;
  }
}

.header-logo {
  position: relative;
  z-index: 10;
  line-height: 0;
  background-repeat: no-repeat;
  background-position: center;
  width: 208px;
  height: 210px;
  margin: 20px 50px 0 0;
  box-sizing: border-box;
  @include mqNotSP {
    background-image: url(../img/logo-octagon.png);
    @include transition;
    transition-property: opacity;
    &:hover {
      opacity: $tr_opacity;
    }
  }
  @include mqTB {
    margin: calc-vw(20,1280) calc-vw(50,1280) 0 0;
    height: calc-vw(210,1280);
    width: calc-vw(208,1280);
    background-size: calc-vw(208,1280) auto;
  }
  @include mqSP {
    height: calc-vw(100);
    width: calc-vw(180);
    margin: 0;
    background-image: url(../img/logo-min.png);
    background-size: calc-vw(180) auto;
    .header_top & {
      margin: calc-vw(30) 0 0 0;
      height: calc-vw(210);
      width: calc-vw(208);
      background-image: url(../img/logo-octagon.png);
      background-size: calc-vw(208) auto;
    }
  }
  a {
    display: block;
    height: 100%;
  }
  .header_simple & {
    margin: 0;
    padding: 0;
    width: 184px;
    height: 48px;
    background-image: url(../img/logo-min.png);
    background-size: 100%;
    @include mqSP {
      position: absolute;
      top: 0;
      width: 92px;
      height: 100%;
      background-size: auto 24px;
    }
  }
  .js-fixedNavi.is-fixed & {
    width: 184px;
    height: 50px;
    margin: 0 0 0 16px;
    background-image: url(../img/logo-min.png);
    background-size: 100%;
    @include mqTB {
      height: calc-vw(50,1280);
      width: calc-vw(180,1280);
      margin: 0 0 0 calc-vw(16,1280);
    }
    @include mqSP {
      width: calc-vw(180);
      background-size: calc-vw(180) auto;
      height: calc-vw(100);
      margin: 0;
    }
  }
}

.header-pagetitle {
  position: absolute;
  right: 20px;
  top: 0;
  font-size: 18px;
  font-weight: bold;
  line-height: 60px;
  @include mqSP {
    right: 15px;
    top: 25px;
    font-size: 12px;
    line-height: 1;
  }
}

.header-navi {
  width: 515px;
  @include mqTB {
    width: calc-vw(515,1280);
  }
}

.header-navi-main {
  margin: 36px 0 11px;
  font-size: 0;
  @include mqTB {
    margin: calc-vw(36,1280) 0 calc-vw(11,1280);
  }
  @include mqSP {
    margin: 0 0 calc-vw(90) 0;
  }
  li {
    @include f_poppins_b;
    font-size: 21px;
    display: inline-block;
    margin-right: 28px;
    &:last-child {
      margin-right: 0;
    }
    @include mqTB {
      margin-right: calc-vw(28,1280);
      font-size: calc-vw(21,1280);
    }
    @include mqSP {
      font-size: calc-vw(32);
      display: block;
      margin-bottom: calc-vw(15);
      &:last-child {
        margin-bottom: 0;
      }
    }
    a {
      @include transition;
      &:hover {
        color: $c_gray2;
      }
    }
  }
  .js-spNavi:not(.is-open) & {
    .js-fixedNavi.is-fixed & {
      margin: 10px 0;
      position: absolute;
      left: 320px;
      @include mqTB {
        margin: calc-vw(10,1280) 0;
        left: calc-vw(320,1280);
      }
    }
  }
}

.header-navi-tags {
  margin-left: -25px;
  font-size: 0;
  @include mqTB {
    margin-left: calc-vw(-25,1280);
  }
  @include mqSP {
    margin-left: calc-vw(-34);
    margin-bottom: calc-vw(72);
  }
  li {
    @include f_poppins;
    font-size: 14px;
    letter-spacing: 0.025em;
    margin-left: 25px;
    display: inline-block;

    @include mqTB {
      font-size: calc-vw(14,1280);
      margin-left: calc-vw(25,1280);
    }
    @include mqSP {
      font-size: calc-vw(24);
      line-height: 2;
      margin-left: calc-vw(36);
    }
    a {
      @include transition;
      &:hover {
        color: $c_gray2;
      }
    }
  }
  .js-spNavi:not(.is-open) & {
    .js-fixedNavi.is-fixed & {
      display: none;
    }
  }
}

.header-navi-register {
  display: flex;
  justify-content: space-between;
  margin-bottom: calc-vw(60);
  li {
    @include mqSP {
      font-size: 13px;
      font-weight: bold;
      display: inline-block;
      a {
        background-color: $c_w;
        color: $c_k;
        padding: calc-vw(14) calc-vw(10) calc-vw(12);
        display: block;
        min-width: calc-vw(170);
        text-align: center;
        box-sizing: border-box;
      }
    }
  }
}

.header-utility-register {
  @include transition;
  font-size: 0;
  li {
    font-size: 14px;
    display: inline-block;
    margin-right: 10px;
    &:last-child {
      margin-right: 0;
    }
    @include mqTB {
      font-size: calc-vw(14,1280);
      margin-right: calc-vw(10,1280);
    }

  }
  .js-fixedNavi.is-fixed & {
    position: absolute;
    right: 60px;
    top: 10px;
    @include mqTB {
      right: calc-vw(60,1280);
      top: calc-vw(10,1280);
    }
  }
}



.header-utility {
  margin: 20px 0 0 auto;
  @include mqTB {
    margin: calc-vw(20,1280) 0 0 auto;
  }
  .js-fixedNavi.is-fixed & {
    margin: 12px 95px 0 auto;
    @include mqTB {
      margin: calc-vw(12,1280) calc-vw(95,1280) 0 auto;
    }

  }
}

.header-utility-info {
  font-size: 0;
  height: 25px;
  position: relative;
  margin-bottom: 25px;
  @include mqTB {
    height: calc-vw(25,1280);
    margin-bottom: calc-vw(25,1280);
  }
  li {
    display: block;
    position: absolute;
    font-size: 14px;
    border-bottom: 1px solid $c_k;
    margin-right: 15px;
    white-space: nowrap;
    &:last-child {
      margin-right: 0;
    }
    @include mqTB {
      font-size: calc-vw(14,1280);
      margin-right: calc-vw(15,1280);
    }
    @include mqSP {
      font-size: 13px;
      font-weight: bold;
    }
    &.header-utility-info-news {
      right: 75px;
      top: 0;
      @include mqTB {
        right: calc-vw(75,1280);
      }
      .js-fixedNavi.is-fixed & {
        display: none;
      }
    }
    &.header-utility-info-search {
      right: 0;
      width: 75px;
      overflow: hidden;
      @include transition;
      transition-property: width;
      cursor: pointer;
      @include mqTB {
        width: calc-vw(75,1280);
      }
      input {
        background-color: transparent;
        width: 100%;
        font-size: 14px;
        box-sizing: border-box;
        opacity: 0;
        @include transition;
        transition-property: opacity;
        @include mqTB {
          font-size: calc-vw(14,1280);
        }
      }
      span {
      }
      .header-search-button {
        position: absolute;
        top: 6px;
        right: 3px;
        pointer-events: none;
        display: inline-block;
        @include mqTB {
          top: calc-vw(6,1280);
          right: 0;
        }
        i {
          &:after {
            @include icofont;
            content: "\e907";
            font-size: 13px;
            @include mqTB {
              font-size: calc-vw(13,1280);
            }          
          }
        }
      }
      &.is-active {
        width: 235px;
        @include mqTB {
          width: calc-vw(235,1280);
        }
        input {
          opacity: 1;
        }
        span {
          display: none;
        }
        .header-search-button {
          pointer-events: auto;
        }
      }
      .js-fixedNavi.is-fixed & {
        position: absolute;
        right: 270px;
        border-bottom: 0;
        width: 25px;
        top: 10px;
        padding: 3px 0;
        z-index: 3;
        html.is-login & {
          right: 290px;
        }
        &:after {
          content: "";
          height: 1px;
          width: 0;
          background-color: $c_k;
          position: absolute;
          right: 0;
          bottom: 0;
        }
        @include mqTB {
          right: calc-vw(280,1280);
          width: calc-vw(25,1280);
          top: calc-vw(10,1280);
          padding: calc-vw(3,1280) 0;
          html.is-login & {
            right: calc-vw(310,1280);
          }
        }
        .header-search-button {
          i {
            &:after {
              font-size: 17px;
              @include mqTB {
