  width: 300px;
  height: 300px;
  margin-bottom: 11px;
  img{
    @include transition;
  }

  &[class*="c-bd-b"]{
    &::after {
      content: "";
      border-style: solid;
      border-width: 5px;
      position: absolute;
      height: 300px;
      width: 300px;
      z-index: 0;
      box-sizing: border-box;
      top: 0;
      left: 0;
      border-color: inherit;
    }
  }

  @include mqTB{
    width: calc-vw(300,1280);
    height: calc-vw(300,1280);
    margin-bottom: calc-vw(11,1280);
    &[class*="c-bd-b"]{
      &::after {
        border-width: calc-vw(5,1280);
        height: calc-vw(300,1280);
        width: calc-vw(300,1280);
      }
    }
  }
  @include mqSP{
    width: calc-vw(140,320);
    height: calc-vw(140,320);
    margin-bottom: calc-vw(4,320);
    &[class*="c-bd-b"]{
      &::after {
        border-width: calc-vw(4,320);
        height: calc-vw(140,320);
        width: calc-vw(140,320);
      }
    }
  }
}

