%input_check {
  display: table;
  position: relative;
  width: 100%;
  // @include mqSP {
  //   display: table;
  // }
  label {
    // display: block;
    display: table-cell;
    vertical-align: middle;
    z-index: 1;
    height: 50px;
    padding: 0 0 0 60px;
    @include f_chikumin;
    font-size: 16px;
    // line-height: 50px;
    cursor: pointer;
    position: relative;
    // @include mqTB {
    //   line-height: 46px;
    // }
    @include mqSP {
      font-size: 14px;
    }
    &:before,
    &:after {
      content: "";
      position: absolute;
      left: 0;
      top: 2px;
      width: 50px;
      height: 50px;
      box-sizing: border-box;
      transition: all .3s $tr_easing;
      cursor: pointer;
    }
    &:before {
      border-radius: 8px;
      border: 2px dashed $c_gray;
    }
    &:after {
      background-image: url(../img/icon-checked.svg);
      background-repeat: no-repeat;
      background-position: center center;
      background-size: auto 24px;
      opacity: 0;
    }
  }
  input[type="checkbox"] {
    position: absolute;
    top: 0;
    left: 0;
    bottom: 0;
    right: 0;
    z-index: 2;
    width: 100%;
    height: 100%;
    opacity: 0;
    cursor: pointer;
    &:checked + label {
      &:before {
        border: 2px solid $c_k;
      }
      &:after {
        opacity: 1;
      }
    }
  }
