.accordion-inner {
  .page-faq & {
    p {
      margin-bottom: 20px;
      color: $c_k;
      &:last-child {
        margin-bottom: 0;
      }
    }
    a {
      color: $c_link;
      text-decoration: underline;
      @include transition;
      &:hover {
        opacity: $tr_opacity;
      }
    }
    ul {
      color: $c_k;
      margin-bottom: 20px;
      font-size: 14px;
      &:last-child {
        margin-bottom: 0;
      }
      @include mqTB {
        margin-bottom: calc-vw(20,1280);
        font-size: calc-vw(14,1280);
      }
      @include mqSP {
        font-size: 13px;
        margin-bottom: calc-vw(40);
      }
      > li{
        margin-bottom: 10px;
        text-indent: -16px;
        padding-left: 16px;
        @include mqTB {
          margin-bottom: calc-vw(10,1280);
        }
        @include mqSP {
          margin-bottom: calc-vw(10);
          text-indent: -16px;
          padding-left: 16px;
        }
        &:before {
          content: "";
          height: 6px;
          width: 6px;
          border-radius: 50%;
          display: inline-block;
          vertical-align: middle;
          margin-right: 10px;
          background-color: $c_k;
        }

        &:last-child {
          margin-bottom: 0;
        }
      }
    }
    ol {
      counter-reset: number;
      margin-bottom: 20px;
      &:last-child {
        margin-bottom: 0;
      }
      @include mqTB{
        margin-bottom: calc-vw(20,1280);
      }
      @include mqSP{
        margin-bottom: calc-vw(40);
      }
      > li {
        font-size: 15px;
        margin-bottom: 10px;
        text-indent: -24px;
        padding-left: 24px;
        @include mqTB {
          font-size: calc-vw(15,1280);
          margin-bottom: calc-vw(10,1280);
        }
        @include mqSP {
          font-size: 13px;
          margin-bottom: calc-vw(10);
          text-indent: -23px;
          padding-left: 23px;
        }
        &:last-child {
          margin-bottom: 0;
        }
        &::before {
          counter-increment: number;
          content: counter(number) ".";
          text-align: center;
          margin-right: 10px;
          font-weight: bold;
        }
      }
    }
  }
}

