.cards_highlight {
  display: flex;
  flex-wrap: wrap;
  margin: 0 20px 60px;
  .page-detail_feature & {
    margin-bottom: 0;
  }
  .gift-past & {
    justify-content: center;
  }
  @include mqTB {
    margin: 0 calc-vw(20,1280) calc-vw(60,1280);
  }
  @include mqSP {
    display: block;
    margin: 0 calc-vw(30);
    padding-bottom: calc-vw(60);
    .page-detail_feature & {
      padding-bottom: 0;
    }
  }
  > li {
    width: 33.3%;
    position: relative;
    .page-detail_feature & {
      background-color: $c_w;
    }
    &:last-child {
      margin-bottom: 0;
    }
    &:nth-child(even) {
      .page-detail_feature & {
        background-color: rgba($c_w,0.7);
      }
    }
    &.is-empty {
      @include mqSP {
        display: none;
      }
    }
    .page-detail_feature & {
      @include mqSP {
        margin-bottom: 0;
      }
    }
    &.is-limited {
      .cards-img-inner {
        &:after {
          background-color: rgba($c_k,0);
        }
      }
      .cards-img {
        &:after {
          content: "";
          width: 90px;
          height: 90px;
          background-image: url(../img/icon-membersonly.svg);
          display: block;
          position: absolute;
          top: 5px;
          right: 5px;
          background-repeat: no-repeat;
          background-position: 0;
          background-size: 90px auto;
          z-index: 3;
          @include mqTB {
            width: calc-vw(90,1280);
            height: calc-vw(90,1280);
            top: calc-vw(5,1280);
            right: calc-vw(5,1280);
            background-size: calc-vw(90,1280) auto;
          }
          @include mqSP {
            width: calc-vw(140);
            height: calc-vw(140);
            top: calc-vw(10);
            right: calc-vw(10);
            background-size: calc-vw(140) auto;
          }
          .page-list_gift & {
            content: none;

          }
        }
      }

    }
    > a {
      display: block;
      &:hover {
        .cards-img-inner {
          &:after {
            background-color: rgba($c_k,.45);
          }
        }
      }
    }
    @include mqSP {
      width: 100%;
      margin-bottom: calc-vw(40);
      &:last-child {
        margin-bottom: 0;
      }
    }
  }
  .cards-img {
    height: auto;
    width: 100%;
    margin-bottom: 0;
    &:before {
      content: "";
      display: block;
      padding-top: 100%;
    }
    &:after {
      content: none;

    }
  }
  .cards-img-inner {
    display: block;
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    img {
      width: 100%;
    }
    &[class*="c-"] {
      &:before {
        content: "";
        height: 90%;
        width: 90%;
        border: 2px solid;
        border-color: inherit;
        position: absolute;
        top: 50%;
        left: 50%;
        transform: translate(-50%,-50%);
        z-index: 3;
      }
    }
    &:after {
      content: "";
      height: 100%;
      width: 100%;
      background-color: rgba($c_k,0.3);
      position: absolute;
      top: 0;
      left: 0;
      z-index: 2;
      @include transition;
    }
  }
  .cards-img-inner-text {
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%,-50%);
    z-index: 4;
    width: 90%;
  }
  .cards-img-date {
    color: $c_w;
    @include f_poppins;
    text-align: center;
    font-size: 13px;
    margin-bottom: 10px;
    @include mqTB {
      font-size: calc-vw(13,1280);
      margin-bottom: calc-vw(10,1280);
    }
    @include mqSP {
      font-size: 11px;
      margin-bottom: calc-vw(5);
    }
  }
  .cards-img-title {
    color: $c_w;
    @include f_chikumin;
    text-align: center;
    font-size: 24px;
    @include mqTB {
      font-size: calc-vw(24,1280);
    }
    @include mqSP {
      font-size: 18px;
    }
  }
  .cards-text {
    padding: 0 20px 35px;
    box-sizing: border-box;
    @include mqTB {
      padding: 0 calc-vw(20,1280) calc-vw(35,1280);
    }
    @include mqSP {
      padding: 0;
    }
    .page-detail_feature & {
      @include mqSP {
        padding: 0 calc-vw(20) calc-vw(40);
      }
    }
    .page-top & {
      padding-bottom: 0;
    }
  }
  .cards-description {
    color: $c_k;
  }
}
.cards-label {
  position: absolute;
  bottom: -15px;
  right: 5px;
  background-color: $c_w;
  border: 4px solid $c_k;
  z-index: 3;
