%input_text {
  width: 100%;
  height: 50px;
  line-height: 50px;
  font-size: 25px;
  transition: all .3s $tr_easing;
  @include f_base;
  background: transparent;
  &:focus,
  &:hover {
    background: $c_gray_light;
  }
  @include placeholder {
    color: $c_gray_placeholder;
    @include f_base;
  }
  @include mqTB {
    font-size: calc-vw(25, 1280);
  }
}
.form-item {
  input[type="text"],
  input[type="number"],
  input[type="email"],
  input[type="password"],
  input[type="tel"] {
    @extend %input_text;
    &.input_s {
      width: 70px;
    }
    &.input_ss {
      width: 50px;
    }
  }
}

%input_radio {
  position: relative;
  float: left;
  height: 50px;
  label {
    display:block;
    height: 50px;
    border-radius: 8px;
    border: 2px dashed $c_gray_placeholder;
    @include f_chikumin;
    font-size: 20px;
    color: $c_gray_placeholder;
    text-align: center;
    line-height: 44px;
    transition: all .3s $tr_easing;
    box-sizing: border-box;
    @include mqTB {
      font-size: calc-vw(20, 1280);
    }
  }
  input[type="radio"] {
    position: absolute;
    left: 0;
    right: 0;
    top: 0;
    bottom: 0;
    z-index:2;
    width: 100%;
    height: 100%;
    opacity: 0;
    cursor: pointer;
    &:checked + label {
      border: 2px solid $c_k;
      color: $c_k;
    }
  }
}
// ラジオボタン
